import { Card, CardContent, CardHeader, CardMedia, List, ListItemButton, Typography } from '@mui/material'
import { create } from '@mui/material/styles/createTransitions'
import React, { useContext, useEffect } from 'react'
import { createAPIEndpoint, ENDPOINTS } from '../api'
import { getFormatedTime } from '../helper'
import useStateContext, { stateContext } from '../hooks/useStateContext'

export default function Quiz() {
  const[qns,setQns] = useState([])
  const[qnIndex, setQnIndex] = useState(0)
  const {context, setContext} = useStateContext()
  const[timeTaken, setTImeTaken] = UseState(0)
  let timer;

const startTimer =()=>{
  timer = setInterval(()=>{
    setTImeTaken(prev => prev +1)
  }, [1000])
}

  useEffect(()=>{
    setContext({
      timeTaken:0,
      selectedOptions:[]
    })
    createAPIEndpoint(ENDPOINTS.question)
    .fetch()
    .then(res =>{
      setQns(res.data)
      startTimer()
    })
    .catch(err=>{console.log(err);})
    return () => {clearInterval(timer)}
  }, [])

  const updateAnswer =(qnId, optionIdx)=>{
    const temp = [...context.selectedOptions]
   temp.push({
    qnId,
    selected:optionIdx
   }) 

   if(qnIndex <4){
    setContext({selectedOptions:[...temp]})
    setQnIndex(qnIndex+1)
   }
   else{
    setContext({selectedOptions:[...temp].timeTaken })
    //navigate result component
   }


  }

  return(
    qns.length !=0
    ?<Card>
      sx ={{maxWidth:640, mx:'auto', mt:5,
    '& .MuiCardHeader-action':{m:0, alignSelf:'center'}}}
      <CardHeader
      title={'Question' + (qnIndex + 1) + 'of 5'}
      action={<Typography>{getFormatedTime(timeTaken)}</Typography>}/>
      <Box>
      <LinearProgress variant="determinate" value={(qnIndex + 1)*100/5} />
      </Box>
      {qns[qnIndex].imageName != null
      ?<CardMedia
    component="img"
  image={BASE_URL + "images/" + qns[qnIndex].imageName}
  sx={{width:'auto', m:'10px auto'}}/>
:null}

      <CardContent>
        <Typography variant='h6'>
          {qns[qnIndex].qnInwords}
        </Typography>
        <List>
          {qns[qnIndex].options.map((item, idx) =>
          <ListItemButton key ={idx} onCLick={()=>updateAnswer(qns[qnIndex].qnId.idx)}>
            <div>
              <b>{String.fromCharCode(65+idx)+" . "}</b>{item}
            </div>
          </ListItemButton>)}
        </List>
      </CardContent>
    </Card>
    : null
  )

  // console.log(context);
  // return (
  //   <div>Question</div>
  // )
}
